// GETTING CONSTANTS
import {
  SLOT_LIST_FAIL,
  SLOT_LIST_REQUEST,
  SLOT_LIST_SUCCESS,
  SLOT_DETAILS_REQUEST,
  SLOT_DETAILS_SUCCESS,
  SLOT_DETAILS_FAIL,
  SLOT_CREATE_REQUEST,
  SLOT_CREATE_SUCCESS,
  SLOT_CREATE_FAIL,
} from '../constants/slotConstants';

// LIST POSTS REDUCER
export const slotListReducer = (state = { allSlots: [] }, action) => {
  switch (action.type) {
    case SLOT_LIST_REQUEST:
      return { loading: true, allSlots: [] };
    case SLOT_LIST_SUCCESS:
      return { loading: false, allSlots: action.payload };
    case SLOT_LIST_FAIL:
      return { loading: false, error: action.payload };
    default:
      return state;
  }
};

// POST DETAILS REDUCER
export const slotDetailsReducer = (state = { slot: null }, action) => {
  switch (action.type) {
    case SLOT_DETAILS_REQUEST:
      return { loading: true, ...state };
    case SLOT_DETAILS_SUCCESS:
      return { loading: false, slot: action.payload };
    case SLOT_DETAILS_FAIL:
      return { loading: false, error: action.payload };
    default:
      return state;
  }
};

// CREATE NEW POST REDUCER
export const slotCreateReducer = (state = {}, action) => {
  switch (action.type) {
    case SLOT_CREATE_REQUEST:
      return { loading: true };
    case SLOT_CREATE_SUCCESS:
      return { loading: false, success: true, slot: action.payload };
    case SLOT_CREATE_FAIL:
      return { loading: false, error: action.payload };
    //   case SLOT_CREATE_RESET:
    //     return {};
    default:
      return state;
  }
};
