import React, { useState, useEffect } from 'react';
import { Form, Button } from 'react-bootstrap';
import { useDispatch, useSelector } from 'react-redux';
import FormContainer from '../components/FormContainer';
import { login } from '../redux/actions/userActions';
import ReactLoading from 'react-loading';
import Message from '../components/Message';

const RegisterScreen = ({ location, history }) => {
  // LOCAL STATES FOR INPUT

  const [mobile, setMobile] = useState('');
  const [message, setMessage] = useState('');

  // DISPATCH
  const dispatch = useDispatch();

  // REGISTER USER REDUCER
  const userLogin = useSelector((state) => state.userLogin);
  const { loading, error, userInfo } = userLogin;

  const redirect = location.search ? location.search.split('=')[1] : '/';

  useEffect(() => {
    if (userInfo) {
      history.push(redirect);
    }
  }, [history, userInfo, redirect]);

  // SUBMIT HANDLER
  const submitHandler = (e) => {
    e.preventDefault();

    if (!mobile) {
      setMessage('mobile number is required');
    } else {
      dispatch(login(mobile));
    }
  };

  return (
    <>
      <FormContainer>
        <h2 className="text-center text-warning my-2">
          <i className="fas fa-pencil-alt text-success"></i> Book Slot
        </h2>

        {message && <Message variant="warning"> {message}</Message>}
        {error && <Message variant="danger"> {error}</Message>}

        {loading && (
          <ReactLoading
            className="m-auto"
            type="spin"
            color="green"
            height={'10%'}
            width={'10%'}
          />
        )}

        <Form onSubmit={submitHandler}>
          <Form.Group controlId="mobile">
            <Form.Label>mobile </Form.Label>
            <Form.Control
              placeholder="enter mobile"
              type="number"
              value={mobile}
              onChange={(e) => setMobile(e.target.value)}
              style={{ borderLeft: '5px solid green' }}
            ></Form.Control>
          </Form.Group>

          <Button type="submit" className="btn btn-block btn-success py-2">
            <i class="fas fa-thumbs-up"></i> Login
          </Button>
          <Button type="btn" className="btn btn-block btn-danger py-2">
            <i class="fas fa-thumbs-up"></i> Cancel
          </Button>
        </Form>
        <Row className="py-3">
          <Col>
            New User ?{' '}
            <Link
              to={redirect ? `/register?redirect=${redirect}` : '/register'}
            >
              Register
            </Link>
          </Col>
        </Row>
      </FormContainer>
    </>
  );
};

export default RegisterScreen;
