import React from 'react';
import { Card, Row, Col } from 'react-bootstrap';
import { LinkContainer } from 'react-router-bootstrap';
const Slots = ({ slot }) => {
  // CONVERT MONGODB DATE TO NORMAL DATE
  //   const convertDate = (input) => {
  //     const date = new Date(input);
  //     return date.toDateString();
  //   };

  return (
    <>
      <LinkContainer to={`/slot/${slot._id}`}>
        <Row>
          <Col className="my-2 py-2">
            <Card className=" shadow rounded text-center bg-light">
              <Card.Body>
                <Card.Header className="bg-success text-warning" as="h4">
                  {`${slot.timing} - ${slot.timing + 1}`}
                </Card.Header>
              </Card.Body>
            </Card>
          </Col>
        </Row>
      </LinkContainer>
    </>
  );
};

export default Slots;
