import React, { useState, useEffect } from 'react';
import { Form, Button } from 'react-bootstrap';
import { useDispatch, useSelector } from 'react-redux';
import FormContainer from '../components/FormContainer';
import { listSlotDetails } from '../redux/actions/slotActions';
import Message from '../components/Message';

const SlotScreen = ({ match, history }) => {
  const [firstname, setFirstName] = useState('');
  const [lastname, setLastName] = useState('');
  const [mobile, setMobile] = useState('');
  const [message, setMessage] = useState('');

  // DISPATCH
  const dispatch = useDispatch();

  const userLogin = useSelector((state) => state.userLogin);
  const { userInfo } = userLogin;

  // CREATE POST REDUCER
  const slotDetails = useSelector((state) => state.slotDetails);
  const { loading, slot, error } = slotDetails;

  useEffect(() => {
    if (!userInfo) {
      history.push('/login');
    } else {
      if (slot?.slotData?._id !== match.params.id) {
        dispatch(listSlotDetails(match.params.id));
      } else {
        setFirstName(slot?.slotData?.firstname);
        setLastName(slot?.slotData?.lastname);
        setMobile(slot?.slotData?.mobile);
      }
    }
  }, [history, userInfo]);

  // SUBMIT HANDLER
  const submitHandler = (e) => {
    e.preventDefault();

    if (!firstname || !lastname || !mobile) {
      setMessage('All fields are required');
    } else {
      // DISPATCH CREATE POST
      //   dispatch(createPost(title, name, content));
      // REDIRECT TO HOMEPAGE
      history.push('/');
    }
  };

  return (
    // LOCAL STATES FOR INPUT

    <>
      <FormContainer>
        <h2 className="text-center text-warning my-2">
          <i className="fas fa-pencil-alt text-success"></i> Book Slot
        </h2>
        {message && <Message variant="warning"> {message}</Message>}
        {error && <Message variant="danger"> {error}</Message>}

        {loading && (
          <ReactLoading
            className="m-auto"
            type="spin"
            color="green"
            height={'10%'}
            width={'10%'}
          />
        )}

        <Form onSubmit={submitHandler}>
          <Form.Group controlId="firstname">
            <Form.Label>firstname </Form.Label>
            <Form.Control
              type="text"
              placeholder="enter firstname"
              value={firstname}
              onChange={(e) => setFirstName(e.target.value)}
              style={{ borderLeft: '5px solid green' }}
            ></Form.Control>
          </Form.Group>

          <Form.Group controlId="lastname">
            <Form.Label>lastname </Form.Label>
            <Form.Control
              type="text"
              placeholder="enter your lastname"
              value={lastname}
              onChange={(e) => setLastName(e.target.value)}
              style={{ borderLeft: '5px solid green' }}
            ></Form.Control>
          </Form.Group>

          <Form.Group controlId="mobile">
            <Form.Label>mobile </Form.Label>
            <Form.Control
              placeholder="enter mobile"
              type="number"
              value={mobile}
              onChange={(e) => setMobile(e.target.value)}
              style={{ borderLeft: '5px solid green' }}
            ></Form.Control>
          </Form.Group>

          <Button type="submit" className="btn btn-block btn-success py-2">
            <i class="fas fa-thumbs-up"></i> Update
          </Button>
          <Button type="btn" className="btn btn-block btn-danger py-2">
            <i class="fas fa-thumbs-up"></i> Cancel
          </Button>
        </Form>
      </FormContainer>
    </>
  );
};

export default SlotScreen;
